    public void comprobarLugarVacio() {
        boolean flag = true;
        int id = 1;
        int ayuda = 0;

        while (flag == true) {
            try {
                File ruta = new File("src\\ArchivosClientes\\" + id + ".txt");

                if (ruta.exists()) {

                    id++;

                } else {
                    ayuda = id + 1;
                    File ruta2 = new File("src\\ArchivosClientes\\" + ayuda + ".txt");

                    if (ruta2.exists()) {
                        ruta2.renameTo(ruta);
                        id++;
                    } else {
                        flag = false;
                    }
                }

            } catch (Exception e) {
                System.out.println(e);
            }
        }
    }

